import React, { useState } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';
import { toast } from 'react-toastify';
import { DocumentTextIcon } from '@heroicons/react/24/solid';

function Link({ setError }) {
  const [parentId, setParentId] = useState('');
  const [billId, setBillId] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      await axios.post('http://localhost:8080/api/link-bag-to-bill', {
        parentId: parseInt(parentId),
        billId,
      });
      setParentId('');
      setBillId('');
      setError(null);
      toast.success('Seed bag linked to bill!', { position: 'top-center' });
    } catch (err) {
      if (err.response?.status === 401) {
        setError('Session expired. Please log in again.');
        toast.error('Session expired. Logging out...', { position: 'top-center' });
      } else {
        setError(err.response?.data?.error || 'Linking failed');
        toast.error(err.response?.data?.error || 'Linking failed', { position: 'top-center' });
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      className="bg-white p-6 rounded-xl shadow-lg border-t-4 border-primary"
    >
      <h2 className="text-2xl font-semibold text-accent mb-4 flex items-center">
        <DocumentTextIcon className="w-6 h-6 text-primary mr-2" />
        Link to Bill
      </h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          value={parentId}
          onChange={(e) => setParentId(e.target.value)}
          placeholder="Parent Bag ID"
          className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary text-accent"
          required
        />
        <input
          value={billId}
          onChange={(e) => setBillId(e.target.value)}
          placeholder="Bill ID"
          className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary text-accent"
          required
        />
        <button
          type="submit"
          disabled={isLoading}
          className={`w-full py-3 rounded-lg text-white flex items-center justify-center ${
            isLoading ? 'bg-gray-400' : 'bg-secondary hover:bg-yellow-600'
          } transition duration-300`}
        >
          {isLoading ? (
            <svg className="animate-spin h-5 w-5 mr-2" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8z" />
            </svg>
          ) : (
            <DocumentTextIcon className="w-5 h-5 mr-2" />
          )}
          Link
        </button>
      </form>
    </motion.div>
  );
}

export default Link;
