import React, { useState } from 'react'; // Add useState import
import axios from 'axios';
import { motion } from 'framer-motion';
import { SparklesIcon } from '@heroicons/react/24/solid';
import { toast } from 'react-toastify';

function Login({ setToken, setRole, setError }) {
  const [localError, setLocalError] = useState(null); // Local error state

  const login = async () => {
    try {
      const res = await axios.post('http://localhost:8080/login', {
        username: 'admin', // Replace with dynamic input in production
        password: 'password',
      });
      setToken(res.data.token);
      localStorage.setItem('token', res.data.token);
      const tokenParts = res.data.token.split('.');
      const payload = JSON.parse(atob(tokenParts[1]));
      setRole(payload.role);
      localStorage.setItem('role', payload.role);
      toast.success('Logged into Star Agriseeds Baggsy!', { position: 'top-center' });
      setLocalError(null);
      setError(null); // Clear any parent error
    } catch (err) {
      const errorMsg = err.response?.data?.error || 'Login failed. Please check backend logs.';
      setLocalError(errorMsg); // Set local error
      setError(errorMsg); // Propagate to App.js
      toast.error(errorMsg, { position: 'top-center' });
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="bg-white rounded-xl shadow-2xl p-8 w-full max-w-md border-t-4 border-primary"
    >
      <div className="flex items-center justify-center mb-6">
        <SparklesIcon className="w-8 h-8 text-primary mr-2" />
        <h1 className="text-3xl font-bold text-accent">Star Agriseeds</h1>
      </div>
      <button
        onClick={login}
        className="w-full bg-primary text-white py-3 rounded-lg hover:bg-green-700 transition duration-300 flex items-center justify-center"
      >
        <SparklesIcon className="w-5 h-5 mr-2" />
        Login to Baggsy
      </button>
      {localError && <p className="text-red-500 text-center mt-4">{localError}</p>} {/* Use localError */}
    </motion.div>
  );
}

export default Login;
