import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Register from './Register';
import Link from './Link';
import Search from './Search';
import axios from 'axios';
import { motion } from 'framer-motion';
import { SparklesIcon } from '@heroicons/react/24/solid'; // Replaced LeafIcon

function App() {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [error, setError] = useState(null);

  useEffect(() => {
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    }
  }, [token]);

  const login = async () => {
    try {
      const res = await axios.post('http://localhost:8080/login', {
        username: 'admin',
        password: 'password',
      });
      setToken(res.data.token);
      localStorage.setItem('token', res.data.token);
      toast.success('Logged into Star Agriseeds Baggsy!', { position: 'top-center' });
      setError(null);
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed');
      toast.error('Login failed. Please try again.', { position: 'top-center' });
    }
  };

  const logout = () => {
    setToken(null);
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    toast.info('Logged out successfully.', { position: 'top-center' });
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-4">
      <ToastContainer />
      {!token ? (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white rounded-xl shadow-2xl p-8 w-full max-w-md border-t-4 border-primary"
        >
          <div className="flex items-center justify-center mb-6">
            <SparklesIcon className="w-8 h-8 text-primary mr-2" /> {/* Replaced LeafIcon */}
            <h1 className="text-3xl font-bold text-accent">Star Agriseeds</h1>
          </div>
          <button
            onClick={login}
            className="w-full bg-primary text-white py-3 rounded-lg hover:bg-green-700 transition duration-300 flex items-center justify-center"
          >
            <SparklesIcon className="w-5 h-5 mr-2" /> {/* Replaced LeafIcon */}
            Login to Baggsy
          </button>
          {error && <p className="text-red-500 text-center mt-4">{error}</p>}
        </motion.div>
      ) : (
        <div className="w-full max-w-5xl">
          <motion.header
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="flex justify-between items-center mb-8 bg-white p-4 rounded-xl shadow-md"
          >
            <div className="flex items-center">
              <SparklesIcon className="w-8 h-8 text-primary mr-2" /> {/* Replaced LeafIcon */}
              <h1 className="text-3xl font-bold text-accent">Star Agriseeds Baggsy</h1>
            </div>
            <button
              onClick={logout}
              className="bg-red-500 text-white py-2 px-4 rounded-lg hover:bg-red-600 transition duration-300"
            >
              Logout
            </button>
          </motion.header>
          {error && <p className="text-red-500 text-center mb-6">{error}</p>}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <Register setError={setError} />
            <Link setError={setError} />
            <Search setError={setError} />
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
