import React, { useState } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';
import { toast } from 'react-toastify';
import { ArchiveBoxIcon } from '@heroicons/react/24/solid';

function Register({ setError }) {
  const [qr, setQr] = useState('');
  const [type, setType] = useState('parent');
  const [childCount, setChildCount] = useState(0);
  const [parentId, setParentId] = useState('');
  const [currentChildren, setCurrentChildren] = useState(0);
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    const bag = { qrCode: qr, type, childCount: type === 'parent' ? parseInt(childCount) : 0 };
    if (type === 'child') bag.parentId = parseInt(parentId);
    try {
      const res = await axios.post('http://localhost:8080/api/register-bag', bag);
      if (type === 'child') {
        setCurrentChildren(currentChildren + 1);
        const parentRes = await axios.get(`http://localhost:8080/api/search/bag?qr=${parentId}`);
        const parentCapacity = parentRes.data.bag.childCount;
        if (currentChildren + 1 >= parentCapacity) {
          setQr('');
          setParentId('');
          setCurrentChildren(0);
          toast.success('Parent bag filled! Ready for next seed batch.', { position: 'top-center' });
        } else {
          toast.success('Child seed bag registered!', { position: 'top-center' });
        }
      } else {
        setQr('');
        setChildCount(0);
        toast.success('Parent seed bag registered!', { position: 'top-center' });
      }
      setError(null);
    } catch (err) {
      if (err.response?.status === 401) {
        setError('Session expired. Please log in again.');
        toast.error('Session expired. Logging out...', { position: 'top-center' });
      } else {
        setError(err.response?.data?.error || 'Registration failed');
        toast.error(err.response?.data?.error || 'Registration failed', { position: 'top-center' });
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      className="bg-white p-6 rounded-xl shadow-lg border-t-4 border-primary"
    >
      <h2 className="text-2xl font-semibold text-accent mb-4 flex items-center">
        <ArchiveBoxIcon className="w-6 h-6 text-primary mr-2" />
        Register Seed Bag
      </h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          value={qr}
          onChange={(e) => setQr(e.target.value)}
          placeholder="Scan QR Code"
          className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary text-accent"
          required
        />
        <div className="flex space-x-2">
          <label className="flex items-center">
            <input
              type="radio"
              value="parent"
              checked={type === 'parent'}
              onChange={(e) => setType(e.target.value)}
              className="mr-2 accent-primary"
            />
            Parent
          </label>
          <label className="flex items-center">
            <input
              type="radio"
              value="child"
              checked={type === 'child'}
              onChange={(e) => setType(e.target.value)}
              className="mr-2 accent-primary"
            />
            Child
          </label>
        </div>
        {type === 'parent' && (
          <input
            type="number"
            value={childCount}
            onChange={(e) => setChildCount(e.target.value)}
            placeholder="Number of Child Bags"
            min="0"
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary text-accent"
            required
          />
        )}
        {type === 'child' && (
          <>
            <input
              value={parentId}
              onChange={(e) => setParentId(e.target.value)}
              placeholder="Parent Bag ID"
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary text-accent"
              required
            />
            {parentId && (
              <div className="w-full bg-gray-200 rounded-full h-3">
                <motion.div
                  className="bg-primary h-3 rounded-full"
                  initial={{ width: 0 }}
                  animate={{ width: `${(currentChildren / (childCount || 1)) * 100}%` }}
                  transition={{ duration: 0.5 }}
                />
              </div>
            )}
            {parentId && (
              <p className="text-sm text-accent">Filled: {currentChildren}/{childCount || 'N/A'}</p>
            )}
          </>
        )}
        <button
          type="submit"
          disabled={isLoading}
          className={`w-full py-3 rounded-lg text-white flex items-center justify-center ${
            isLoading ? 'bg-gray-400' : 'bg-primary hover:bg-green-700'
          } transition duration-300`}
        >
          {isLoading ? (
            <svg className="animate-spin h-5 w-5 mr-2" viewBox="0 0 24 24">
              <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
              <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8v8z" />
            </svg>
          ) : (
            <ArchiveBoxIcon className="w-5 h-5 mr-2" />
          )}
          Register
        </button>
      </form>
    </motion.div>
  );
}

export default Register;
