import React, { useState } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';
import { toast } from 'react-toastify';
import { MagnifyingGlassIcon } from '@heroicons/react/24/solid';

function Search({ setError }) {
  const [qr, setQr] = useState('');
  const [bill, setBill] = useState('');
  const [result, setResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const searchByQr = async () => {
    setIsLoading(true);
    try {
      const res = await axios.get(`http://localhost:8080/api/search/bag?qr=${qr}`);
      setResult(res.data);
      setError(null);
      toast.success('Seed bag found!', { position: 'top-center' });
    } catch (err) {
      if (err.response?.status === 401) {
        setError('Session expired. Please log in again.');
        toast.error('Session expired. Logging out...', { position: 'top-center' });
      } else {
        setError(err.response?.data?.error || 'Search failed');
        toast.error(err.response?.data?.error || 'Search failed', { position: 'top-center' });
      }
    } finally {
      setIsLoading(false);
    }
  };

  const searchByBill = async () => {
    setIsLoading(true);
    try {
      const res = await axios.get(`http://localhost:8080/api/search/bill?bill=${bill}`);
      setResult(res.data);
      setError(null);
      toast.success('Bill details retrieved!', { position: 'top-center' });
    } catch (err) {
      if (err.response?.status === 401) {
        setError('Session expired. Please log in again.');
        toast.error('Session expired. Logging out...', { position: 'top-center' });
      } else {
        setError(err.response?.data?.error || 'Search failed');
        toast.error(err.response?.data?.error || 'Search failed', { position: 'top-center' });
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      className="bg-white p-6 rounded-xl shadow-lg border-t-4 border-primary"
    >
      <h2 className="text-2xl font-semibold text-accent mb-4 flex items-center">
        <MagnifyingGlassIcon className="w-6 h-6 text-primary mr-2" />
        Search Seed Bags
      </h2>
      <div className="space-y-4">
        <div className="flex space-x-2">
          <input
            value={qr}
            onChange={(e) => setQr(e.target.value)}
            placeholder="Scan Bag QR"
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary text-accent"
          />
          <button
            onClick={searchByQr}
            disabled={isLoading || !qr}
            className={`py-3 px-4 rounded-lg text-white ${
              isLoading || !qr ? 'bg-gray-400' : 'bg-primary hover:bg-green-700'
            } transition duration-300`}
          >
            {isLoading && qr ? '...' : 'QR'}
          </button>
        </div>
        <div className="flex space-x-2">
          <input
            value={bill}
            onChange={(e) => setBill(e.target.value)}
            placeholder="Enter Bill ID"
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary text-accent"
          />
          <button
            onClick={searchByBill}
            disabled={isLoading || !bill}
            className={`py-3 px-4 rounded-lg text-white ${
              isLoading || !bill ? 'bg-gray-400' : 'bg-secondary hover:bg-yellow-600'
            } transition duration-300`}
          >
            {isLoading && bill ? '...' : 'Bill'}
          </button>
        </div>
        {result && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="mt-4 p-4 bg-background rounded-lg border border-primary"
          >
            <pre className="text-sm text-accent font-mono">{JSON.stringify(result, null, 2)}</pre>
          </motion.div>
        )}
      </div>
    </motion.div>
  );
}

export default Search;
